import React, { useState } from 'react';
import { getProjects } from '../Services/api';
import Loader from './Loader';

const Step1Connect = ({ setProjects, setError, onConnected }) => {
  const [isLoading, setIsLoading] = useState(false);

  const handleConnect = async () => {
    setIsLoading(true);
    setError('');
    try {
      const response = await getProjects();
      setProjects(response.data);
      onConnected();
    } catch (err) {
      setError('Failed to connect to Azure DevOps. Check your backend server and PAT.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-700 mb-4">Step 1: Connect to Azure DevOps</h2>
      <p className="text-gray-600 mb-6">Click the button to fetch available projects. Ensure your backend server is running with the correct credentials.</p>
      {isLoading ? (
        <Loader text="Fetching projects..." />
      ) : (
        <button
          onClick={handleConnect}
          className="w-full bg-blue-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-blue-700 transition duration-300"
        >
          Connect and Fetch Projects
        </button>
      )}
    </div>
  );
};

export default Step1Connect;
