import React, { useState, useEffect } from 'react';
import { getSprints, getUserStories } from '../Services/api';
import Loader from './Loader';

const Step2SelectItems = ({ projects, setSelectedProject, setSelectedUserStory, onSelected, setError }) => {
  const [sprints, setSprints] = useState([]);
  const [userStories, setUserStories] = useState([]);
  
  const [selectedProjectId, setSelectedProjectId] = useState('');
  const [selectedSprintPath, setSelectedSprintPath] = useState('');
  const [selectedStoryId, setSelectedStoryId] = useState('');

  const [loading, setLoading] = useState({ sprints: false, stories: false });

  useEffect(() => {
    if (selectedProjectId) {
      const project = projects.find(p => p.id === selectedProjectId);
      setSelectedProject(project);

      const fetchSprints = async () => {
        setLoading(prev => ({ ...prev, sprints: true }));
        setError('');
        try {
          const response = await getSprints(project.name);
          setSprints(response.data);
        } catch (err) {
          setError('Failed to fetch sprints.');
        } finally {
          setLoading(prev => ({ ...prev, sprints: false }));
        }
      };
      fetchSprints();
    }
  }, [selectedProjectId, projects, setSelectedProject, setError]);

  useEffect(() => {
    if (selectedProjectId && selectedSprintPath) {
      const project = projects.find(p => p.id === selectedProjectId);
      const fetchStories = async () => {
        setLoading(prev => ({ ...prev, stories: true }));
        setError('');
        try {
          const response = await getUserStories(project.name, selectedSprintPath);
          setUserStories(response.data);
        } catch (err) {
          setError('Failed to fetch user stories.');
        } finally {
          setLoading(prev => ({ ...prev, stories: false }));
        }
      };
      fetchStories();
    }
  }, [selectedProjectId, selectedSprintPath, projects, setError]);
  
  const handleStorySelection = (storyId) => {
      const story = userStories.find(s => s.id.toString() === storyId);
      setSelectedUserStory(story);
      setSelectedStoryId(storyId);
  }

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-700 mb-4">Step 2: Select Your Items</h2>
      <div className="space-y-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Project</label>
          <select onChange={(e) => setSelectedProjectId(e.target.value)} className="w-full p-2 border border-gray-300 rounded-md">
            <option value="">-- Select a Project --</option>
            {projects.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
          </select>
        </div>

        {loading.sprints ? <Loader text="Fetching sprints..." /> : sprints.length > 0 && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Sprint</label>
            <select onChange={(e) => setSelectedSprintPath(e.target.value)} className="w-full p-2 border border-gray-300 rounded-md">
              <option value="">-- Select a Sprint --</option>
              {sprints.map(s => <option key={s.id} value={s.id}>{s.name}</option>)}
            </select>
          </div>
        )}

        {loading.stories ? <Loader text="Fetching user stories..." /> : userStories.length > 0 && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">User Story</label>
            <select onChange={(e) => handleStorySelection(e.target.value)} className="w-full p-2 border border-gray-300 rounded-md">
              <option value="">-- Select a User Story --</option>
              {userStories.map(s => <option key={s.id} value={s.id}>{s.title}</option>)}
            </select>
          </div>
        )}
      </div>
      
      {selectedStoryId && (
        <button
          onClick={onSelected}
          className="mt-8 w-full bg-blue-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-blue-700 transition duration-300"
        >
          Next: Analyze User Story
        </button>
      )}
    </div>
  );
};

export default Step2SelectItems;
