import React, { useState } from 'react';

function Step1_Connect({ setProjects, setError, onConnected }) {
  const [orgUrl, setOrgUrl] = useState('');
  const [pat, setPat] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleConnect = async (e) => {
    e.preventDefault();
    if (!orgUrl || !pat) {
      setError('Please provide both the Organization URL and a PAT.');
      return;
    }
    setError('');
    setIsLoading(true);

    try {
      const response = await fetch('/api/connect', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ado_organization_url: orgUrl,
          ado_pat: pat,
        }),
      });

      const contentType = response.headers.get('content-type');
      if (!response.ok || !contentType || contentType.indexOf('application/json') === -1) {
        const text = await response.text();
        try {
          // Try to parse as a JSON error from our backend
          const errorData = JSON.parse(text);
          throw new Error(errorData.detail || `Server error: ${response.status}`);
        } catch (jsonError) {
          // If it's not JSON, it's likely an HTML page from a proxy error or server crash
          throw new Error(
            'Received an invalid response from the server (expected JSON). Please ensure the backend is running and restart the frontend server.'
          );
        }
      }

      const data = await response.json();
      setProjects(data.projects);
      sessionStorage.setItem('ado_organization_url', orgUrl);
      sessionStorage.setItem('ado_pat', pat);
      onConnected();
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleConnect} className="space-y-6">
      <div>
        <label htmlFor="orgUrl" className="block text-sm font-medium text-gray-700">
          Azure DevOps Organization URL
        </label>
        <div className="mt-1">
          <input
            type="text"
            id="orgUrl"
            value={orgUrl}
            onChange={(e) => setOrgUrl(e.target.value)}
            placeholder="https://dev.azure.com/your-org"
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            required
          />
        </div>
      </div>

      <div>
        <label htmlFor="pat" className="block text-sm font-medium text-gray-700">
          Personal Access Token (PAT)
        </label>
        <div className="mt-1">
          <input
            type="password"
            id="pat"
            value={pat}
            onChange={(e) => setPat(e.target.value)}
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            required
          />
        </div>
      </div>

      <div>
        <button
          type="submit"
          disabled={isLoading}
          className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:bg-blue-400 disabled:cursor-not-allowed"
        >
          {isLoading ? 'Connecting...' : 'Connect'}
        </button>
      </div>
    </form>
  );
}

export default Step1_Connect;
