import React, { useState } from 'react';

function Step1Connect({ setProjects, setError, onConnected }) {
  const [orgUrl, setOrgUrl] = useState('');
  const [pat, setPat] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      // This endpoint will receive the credentials and use them to fetch projects.
      const response = await fetch('/api/connect', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ado_organization_url: orgUrl,
          ado_pat: pat,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to connect to Azure DevOps.');
      }

      const data = await response.json();
      // The backend should return a list of projects.
      setProjects(data.projects);

      // Store credentials in session storage to be used by other steps.
      // Note: Storing PATs in sessionStorage is convenient for development but
      // has security implications. For production, consider more secure storage.
      sessionStorage.setItem('ado_organization_url', orgUrl);
      sessionStorage.setItem('ado_pat', pat);

      onConnected();
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div>
        <label htmlFor="orgUrl" className="block text-sm font-medium text-gray-700">
          Azure DevOps Organization URL
        </label>
        <div className="mt-1">
          <input
            type="text"
            name="orgUrl"
            id="orgUrl"
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="https://dev.azure.com/your-organization"
            value={orgUrl}
            onChange={(e) => setOrgUrl(e.target.value)}
            required
          />
        </div>
      </div>

      <div>
        <label htmlFor="pat" className="block text-sm font-medium text-gray-700">
          Personal Access Token (PAT)
        </label>
        <div className="mt-1">
          <input
            type="password"
            name="pat"
            id="pat"
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter your PAT"
            value={pat}
            onChange={(e) => setPat(e.target.value)}
            required
          />
        </div>
      </div>

      <div>
        <button
          type="submit"
          disabled={isLoading || !orgUrl || !pat}
          className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:bg-blue-400 disabled:cursor-not-allowed"
        >
          {isLoading ? 'Connecting...' : 'Connect and Fetch Projects'}
        </button>
      </div>
    </form>
  );
}

export default Step1Connect;
