import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Loader from './Loader';

const Step2SelectItems = ({ projects, setSelectedProject, setSelectedUserStory, onSelected, setError }) => {
  const [sprints, setSprints] = useState([]);
  const [userStories, setUserStories] = useState([]);

  const [selectedProjectId, setSelectedProjectId] = useState('');
  const [selectedSprintPath, setSelectedSprintPath] = useState('');
  const [selectedStoryId, setSelectedStoryId] = useState('');

  const [loading, setLoading] = useState({ sprints: false, stories: false });

  // Fetch sprints when a project is selected
  useEffect(() => {
    // Clear downstream state when project changes
    setSprints([]);
    setUserStories([]);
    setSelectedSprintPath('');
    setSelectedStoryId('');
    setSelectedUserStory(null);

    if (selectedProjectId) {
      const project = projects.find(p => p.id === selectedProjectId);
      setSelectedProject(project);

      const fetchSprints = async () => {
        setLoading(prev => ({ ...prev, sprints: true }));
        setError('');
        try {
          const orgUrl = sessionStorage.getItem('ado_organization_url');
          const pat = sessionStorage.getItem('ado_pat');
          const response = await axios.post('/api/sprints', {
            ado_organization_url: orgUrl,
            ado_pat: pat,
            project_id: selectedProjectId,
          });
          setSprints(response.data.sprints);
        } catch (err) {
          if (axios.isAxiosError(err) && err.response) {
            setError(err.response.data.detail || `Failed to fetch sprints. Status: ${err.response.status}`);
          } else {
            setError('An unexpected error occurred while fetching sprints.');
          }
        } finally {
          setLoading(prev => ({ ...prev, sprints: false }));
        }
      };
      fetchSprints();
    }
  }, [selectedProjectId, projects, setSelectedProject, setError]);

  // Fetch user stories when a sprint is selected
  useEffect(() => {
    // Clear downstream state when sprint changes
    setUserStories([]);
    setSelectedStoryId('');
    setSelectedUserStory(null);

    if (selectedProjectId && selectedSprintPath) {
      const fetchStories = async () => {
        setLoading(prev => ({ ...prev, stories: true }));
        setError('');
        try {
          const orgUrl = sessionStorage.getItem('ado_organization_url');
          const pat = sessionStorage.getItem('ado_pat');
          const response = await axios.post('/api/user-stories', {
            ado_organization_url: orgUrl,
            ado_pat: pat,
            project_id: selectedProjectId,
            sprint_path: selectedSprintPath,
          });
          setUserStories(response.data.user_stories);
        } catch (err) {
          if (axios.isAxiosError(err) && err.response) {
            setError(err.response.data.detail || `Failed to fetch user stories. Status: ${err.response.status}`);
          } else {
            setError('An unexpected error occurred while fetching user stories.');
          }
        } finally {
          setLoading(prev => ({ ...prev, stories: false }));
        }
      };
      fetchStories();
    }
  }, [selectedProjectId, selectedSprintPath, setSelectedUserStory, setError]);

  const handleStorySelection = (storyId) => {
      const story = userStories.find(s => s.id.toString() === storyId);
      setSelectedUserStory(story);
      setSelectedStoryId(storyId);
  }

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-700 mb-4">Step 2: Select Your Items</h2>
      <div className="space-y-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Project</label>
          <select value={selectedProjectId} onChange={(e) => setSelectedProjectId(e.target.value)} className="w-full p-2 border border-gray-300 rounded-md">
            <option value="">-- Select a Project --</option>
            {projects.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
          </select>
        </div>

        {loading.sprints ? <Loader text="Fetching sprints..." /> : sprints.length > 0 && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Sprint</label>
            <select value={selectedSprintPath} onChange={(e) => setSelectedSprintPath(e.target.value)} className="w-full p-2 border border-gray-300 rounded-md">
              <option value="">-- Select a Sprint --</option>
              {sprints.map(s => <option key={s.id} value={s.path}>{s.name}</option>)}
            </select>
          </div>
        )}

        {loading.stories ? <Loader text="Fetching user stories..." /> : userStories.length > 0 && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">User Story</label>
            <select value={selectedStoryId} onChange={(e) => handleStorySelection(e.target.value)} className="w-full p-2 border border-gray-300 rounded-md">
              <option value="">-- Select a User Story --</option>
              {userStories.map(s => <option key={s.id} value={s.id}>{s.title}</option>)}
            </select>
          </div>
        )}
      </div>

      {selectedStoryId && (
        <button
          onClick={onSelected}
          className="mt-8 w-full bg-blue-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-blue-700 transition duration-300"
        >
          Next: Analyze User Story
        </button>
      )}
    </div>
  );
};

export default Step2SelectItems;
